//#include <linux/config.h>#include <linux/module.h>#include <linux/moduleparam.h>#include <linux/init.h>#include <linux/kernel.h>	/* printk() */#include <linux/slab.h>		/* kmalloc() */#include <linux/fs.h>		/* everything... */ //register_chrdev_region#include <linux/errno.h>	/* error codes */#include <linux/types.h>	/* size_t */#include <linux/proc_fs.h>#include <linux/fcntl.h>	/* O_ACCMODE */#include <linux/seq_file.h>#include <linux/cdev.h>#include <asm/system.h>		/* cli(), *_flags */#include <asm/uaccess.h>	/* copy_*_user */#include "scull.h"		/* local definitions */int scull_major =   SCULL_MAJOR;int scull_minor =   0;int scull_nr_devs = SCULL_NR_DEVS;	/* number of bare scull devices */int scull_quantum = SCULL_QUANTUM;int scull_qset =    SCULL_QSET;module_param(scull_major, int, S_IRUGO);module_param(scull_minor, int, S_IRUGO);module_param(scull_nr_devs, int, S_IRUGO);module_param(scull_quantum, int, S_IRUGO);module_param(scull_qset, int, S_IRUGO);MODULE_AUTHOR("Alessandro Rubini, Jonathan Corbet");MODULE_LICENSE("Dual BSD/GPL");struct scull_dev *scull_devices;	/* allocated in scull_init_module *//* * Open and close *//* * Empty out the scull device; must be called with the device * semaphore held. */int scull_open(struct inode *inode, struct file *filp){	struct scull_dev *dev; /* device information */	dev = container_of(inode->i_cdev, struct scull_dev, cdev);//调用container_of宏，通过cdev成员得到包含该cdev的scull_dev结构。	filp->private_data = dev; /* for other methods filp->can use in other func*/        int i=0;        for(i=0;i<10;i++)dev->mem[i]=i+96;	return 0;          /* success */}int scull_release(struct inode *inode, struct file *filp){	return 0;}/* * Data management: read and write */ssize_t scull_read(struct file *filp, char __user *buf, size_t count,                loff_t *f_pos){	struct scull_dev *dev = filp->private_data;         ssize_t retval = -ENOMEM; /* value used in "goto out" statements */	/* find listitem, qset index, and offset in the quantum */	if (copy_to_user(buf,dev->mem,count)) {//参数2传递的是起始位置		retval = -EFAULT;		goto out;	}	retval=count;  out:	return retval;}ssize_t scull_write(struct file *filp, const char __user *buf, size_t count,                loff_t *f_pos){	struct scull_dev *dev = filp->private_data;	ssize_t retval = -ENOMEM; /* value used in "goto out" statements */		if (copy_from_user(dev->mem, buf, count)) {		retval = -EFAULT;		goto out;	}	retval=count;	  out:	return retval;}/* * The "extended" operations -- only seek */loff_t scull_llseek(struct file *filp, loff_t off, int whence){	int newpos=4;	return newpos;}struct file_operations scull_fops = {	.owner =    THIS_MODULE,	//.llseek =   scull_llseek,	.read =     scull_read,	.write =    scull_write,	//.ioctl =    scull_ioctl,	.open =     scull_open,	.release =  scull_release,};/* * Set up the char_dev structure for this device. */static void scull_setup_cdev(struct scull_dev *dev, int index){	int err, devno = MKDEV(scull_major, scull_minor + index);    	cdev_init(&dev->cdev, &scull_fops);	dev->cdev.owner = THIS_MODULE;	dev->cdev.ops = &scull_fops;	err = cdev_add (&dev->cdev, devno, 1);	/* Fail gracefully if need be */	if (err)		printk(KERN_NOTICE "Error %d adding scull%d", err, index);}/* * The cleanup function is used to handle initialization failures as well. * Thefore, it must be careful to work correctly even if some of the items * have not been initialized */void scull_cleanup_module(void){	int i;	dev_t devno = MKDEV(scull_major, scull_minor);	/* Get rid of our char dev entries */	if (scull_devices) {		cdev_del(&scull_devices->cdev);		kfree(scull_devices);	}	/* cleanup_module is never called if registering failed */	unregister_chrdev_region(devno, scull_nr_devs);}int scull_init_module(void){	int result, i;	dev_t dev = 0;/* * Get a range of minor numbers to work with, asking for a dynamic * major unless directed otherwise at load time. */	if (scull_major) {		dev = MKDEV(scull_major, scull_minor);		result = register_chrdev_region(dev, scull_nr_devs, "scull");	} else {		result = alloc_chrdev_region(&dev, scull_minor, scull_nr_devs,				"scull");		scull_major = MAJOR(dev);	}	if (result < 0) {		printk(KERN_WARNING "scull: can't get major %d\n", scull_major);		return result;	}        /* 	 * allocate the devices -- we can't have them static, as the number	 * can be specified at load time	 */	scull_devices = kmalloc(scull_nr_devs * sizeof(struct scull_dev), GFP_KERNEL);	if (!scull_devices) {		result = -ENOMEM;		goto fail;  /* Make this more graceful */	}	memset(scull_devices, 0, scull_nr_devs * sizeof(struct scull_dev));        scull_setup_cdev(&scull_devices, 0);	return 0; /* succeed */  fail:	scull_cleanup_module();	return result;}module_init(scull_init_module);module_exit(scull_cleanup_module);